export const layout = {
    canvas : {
        height: "500",
        width: "500",
    },

    Appmain : {
        backgroundColor: "#320453",
        height: "100vh",
        width: "100vw",
    },

    text: {
        position: "absolute",
        left: 50,
        top: 600,
        color: "yellow",
        backgroundColor: "#320453",
        width: 80,
    },

    buttons: {
        position: "absolute",
        left: 600,
        top: 10,
    },

    upbutton: {
        position: "absolute",
        left: 50,
        top: 80,
    },

    downbutton: {
        position: "absolute",
        left: 50,
        top: 120,
    },

    leftbutton: {
        position: "absolute",
        top: 100,
    },

    rightbutton: {
        position: "absolute",
        top: 100,
        left: 100,
    },

    resetbutton: {
        position: "absolute",
        left: 40,
        top: 40,
    }
}
